import React from 'react'
import { connect } from 'react-redux'
import { addTodo } from '../actions'
import { DatePicker, Form, Input, Button, Row, Col } from 'antd';
import "antd/dist/antd.css";

const AddTodo = ({dispatch}) => {
  let input
  return (
      <Col lg={{span:14}}>
        <Form className="form-custom" onSubmit={e => {
            e.preventDefault(); 
            if(!input.value.trim()){
              return
            }
            dispatch(addTodo(input.value))
            input.value =''
          }}>
          <Form.Item rules={[{required:true,message:'Please fll to input'}]}>
          <Input className="input-task" placeholder="Enter a task..." type="text" 
             ref={node => (input = node)}
          />
          <DatePicker className="input-task ml-10px" />
          <Button htmlType="submit" className="btn-add" >Add Tasks</Button>
          </Form.Item>
        </Form>
      </Col>
  )
}

export default connect()(AddTodo)
