import React, { Component } from 'react'
import { v4 as uuidv4 } from 'uuid';

function DateTime(){
  const options = { weekday: 'long', month: 'long', day: 'numeric' };
  const date = new Date().toLocaleDateString(undefined,options);
  return(
    <div className="date">{date}</div>
  )
}

class MainTasks extends React.Component{
  constructor(props){
    super(props);
    this.handleAdd = this.handleAdd.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.state={
      tasks: [
        {id: 1, content: 'Hello'}
      ],
      text: '',
  }
}
  handleChange(e){
    this.setState({
      text: e.target.value
    })
  }
  handleAdd(e){
    e.preventDefault();
    let task = {
      id: uuidv4(),
      content: this.state.text,
    }
    let tasks = [...this.state.tasks];
    tasks.push(task);

    this.setState({tasks: tasks});

    this.setState({text:''});
  }
  render(){
    return (
      <div className="row">
        <div className="col-lg-7">
          <form className="form-custom" onSubmit={this.handleAdd}>
            <input className="input-task" placeholder="Enter a task..." type="text" 
              onChange={this.handleChange}
              value={this.state.text}

            />
            <button type="submit" className="btn-add" >Add Tasks</button>
          </form>
        </div>
        <div className="col-lg-5"></div>
        <div className="col-lg-12">
          {
            this.state.tasks.map(value => 
            <div key={value.id} className="taskk"> {value.content} 
            </div>)
          }
        </div>
      </div>
    )
  }
}

class App extends Component {
  render() {
    return (
      <div className='row'>
        <div className='col-lg-12'>
          <div className='d-md-flex flex-column justify-content-center custom-bg'>
            <div className='row'>
              <div className='col-lg-10 offset-lg-1 bg-black'>
                  <div className="row">
                    <div className="col-lg-7">
                      <div className="heading-left">
                        <DateTime/>
                      </div>
                    </div>
                    <div className="col-lg-5">
                      <div className="row">
                        <div className="col-lg-6 task">Incomplete Tasks</div>
                        <div className="col-lg-6 task">Complete Tasks</div>
                      </div>
                    </div>
                  </div>

                        <MainTasks/>

              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default App
