import React from 'react'
import Todo from './Todo'
import {connect} from 'react-redux'
import { Col, Row } from 'antd';
import "antd/dist/antd.css"; 
import classnames from 'classnames'

var status = 1;

export const ToggleFilter = () =>{
  return(
      <Row>
      <Col md={{span:12}} className={classnames(' text-center task-filter')} onClick={()=>status = 1}>Incomplete Tasks</Col>
      <Col md={{span:12}} className={classnames(' text-center task-filter')} onClick={()=>status = 1}>Completed Tasks </Col>
    </Row>
  )
}

const TodoList = ({todos}) => {
  console.log('Task',todos)
  return(
    <Col lg={{span:24}} className=" mt-lg-4" >
    {
      todos.map(value => {
        if(value.status===status){
          return(
            <Todo
                key = {value.id}
                {...value}
            />

        )}
        return null
      })
    }
  </Col>
)}

const mapState2Props = (state) => {
  return {
    todos: state.todos
  }
}

export default connect(mapState2Props)(TodoList)
