import React from 'react'
import { connect } from 'react-redux'
import { addTodo } from '../actions'
import { DatePicker, Form, Input, Button, Col } from 'antd';
import "antd/dist/antd.css";

const AddTodo = ({dispatch}) => {
  const onOk = (content,values) =>{
    form.submit();
    console.log(values,values);
    if(values === {}){
      return
    }
    dispatch(addTodo(content,values))
  }
  return (
      <Col lg={{span:14, offset:1}}>
        <Form className="form-custom"
          onOK={onOk()}
        >
          <Input className="input-task" placeholder="Enter a task..." type="text" />
          <DatePicker className="input-task ml-10px" />
          <Button htmlType="submit" className="btn-add" >Add Tasks</Button>
        </Form>
      </Col>
  )
}

export default connect()(AddTodo)
