const todos = (state = [], action) => {
    switch(action.type){
        case 'ADD_TODO':
            return [
                ...state,
                {
                    id:action.id,
                    content: action.text,
                    status:0,
                    created: new Date().toLocaleDateString(),
                    dead: action.dead
                }
            ]
        case 'COMPLETE_TODO':
            return state.map(todo => todo.id === action.id ? { ...todo, status:1  } : todo )
        case 'DELETE_TODO':
            return state.map(todo => todo.id === action.id ? state.filter(item => item !== todo) : todo)
        default:
            return state
    }
}

export default todos
