import React from 'react'
import { connect } from 'react-redux'
import { addTodo } from '../actions'
import { DatePicker } from 'antd';
import "antd/dist/antd.css";

function onChange(date, dateString) {
  console.log(date, dateString);
}

const AddTodo = ({dispatch}) => {
  let input
  let deadline
  return (
      <div className="col-lg-7">
        <form className="form-custom" onSubmit={e => {
            e.preventDefault(); 
            if(!input.value.trim() || isNaN(Date.parse(deadline.value)) === true){
              return
            }
            dispatch(addTodo(input.value,deadline.value))
            input.value =''
            deadline.value=''
          }}>
          <input className="input-task" placeholder="Enter a task..." type="text" 
             ref={node => (input = node)}
          />
          <DatePicker ref={dead => (deadline = dead)} className="input-task ml-10px" onChange={onChange}/>

          <button type="submit" className="btn-add" >Add Tasks</button>
        </form>
      </div>
  )
}

export default connect()(AddTodo)
