export const addTodo = (content,id) => ({
    type:'ADD_TODO',
    content,
    id
})

export const completeTodo = id => ({
    type:"COMPLETE_TODO",
    id
})

export const deleteTodo = id  => ({
    type:"DELETE_TODO",
    id
})

export const editTodo = (action) => ({
    type:"EDIT_TODO",
    payload: action
})

export const toggleFilter = status => ({
    type:"TOGGLE_FILTER",
    status
})

