import React from 'react'
import classnames from 'classnames'
import { Row, Col } from 'antd';

const ToggleFilter = () =>{
    let filter = 1;
    return(
        <div className="row">
        <div className={classnames('col-md-6 text-center task-filter', filter === 1 ? 'active' : '')} onClick={() => filter = 1 }>Incomplete Tasks</div>
        <div className={classnames('col-md-6 text-center task-filter', filter === 2 ? 'active' : '')} onClick={() => filter = 2}>Completed Tasks </div>
      </div>
    )
}

export default ToggleFilter
