const todos = (state = [], action) => {
    switch(action.type){
        case 'ADD_TODO':
            return [
                ...state,
                {
                    id:action.id,
                    content: action.content,
                    status:false,
                    created: new Date().toLocaleDateString(),
                    dead: action.dead
                }
            ]
        case 'COMPLETE_TODO':
            return state.map(todo => todo.id === action.id ? { ...todo, status:!todo.status  } : todo )
        case 'DELETE_TODO':
            return state.map(todo => todo.id === action.id ? state.filter(item => item !== todo) : todo)
        case 'EDIT_TODO':
            return state.map(todo => todo.id === action.id ? {...todo,content:action.text,dead:action.dead} : todo)
        case 'TOGGLE_FILTER':
            return state.filter(item => item.status === action.status)
        default:
            return state
    }
}

export default todos
