import React from 'react'
import PropTypes from 'prop-types'
import classnames from 'classnames'
import {completeTodo, deleteTodo} from '../actions'
import { connect } from 'react-redux'
import EditTodo from './EditTodo'


const Todo = ({status,content,id,created,dead,dispatch}) => { 
    return (
        <div className= "row taskk" >
            <div className="col-md-1">
                <i className="fa fa-check i-check" aria-hidden="true" onClick={() => dispatch(completeTodo(id))}></i>
            </div>
            <div className={classnames(" col-md-6 task-edit")}>{content}</div>
            {/* <div className={classnames('col-md-6 task-edit',show === true ? 'd-static' :'d-none')} >
                <EditTodo 
                    id={id}
                    content = {content}
                />
            </div> */}
            <div className=" col-md-2 text-center" > {created} </div>
            <div className=" col-md-2 text-center" > {dead} </div>
            <div className="col-md-1 text-right">
                {/* <a href="#" className={classnames('i-trash mr-md-3',value.status === 1 && 'd-lg-none')}><i className="fa fa-pencil " aria-hidden="true"></i></a> */}
                <a href="#" className={classnames('i-trash',status === true && 'd-lg-none')} onClick={() => dispatch(deleteTodo(id))}><i className="fa fa-trash-o " aria-hidden="true"></i></a>
            </div>
        </div>
    )
}

Todo.propTypes = {
    id:PropTypes.string.isRequired,
    content:PropTypes.string.isRequired,
    status:PropTypes.bool.isRequired,
    created:PropTypes.string.isRequired,
    dead:PropTypes.string.isRequired,}

export default connect() (Todo)
