function FmDate(props) {
  return <h2>It is {props.date.toLocaleTimeString()}.</h2>;
}
class Clock extends React.Component{
  constructor(props){
    super(props);
    this.state={date: new Date()}
  }
  componentDidMount(){
    this.timerID = setInterval( () => this.tick(),1000)
  }
  componentWillMount(){
    clearInterval(this.timerID)
  }

  tick(){
    this.setState({
      date: new Date()
    })
  }

  render(){
    return(
      <div>
        <h1>Hello, world!</h1>
        <FmDate date={this.state.date}/>
      </div>
    )
  }
}

