import React, { Component } from 'react'
import { v4 as uuidv4 } from 'uuid';
import classnames from 'classnames'

function DateTime(){
  const options = { weekday: 'long', month: 'long', day: 'numeric' };
  const date = new Date().toLocaleDateString(undefined,options);
  return(
    <div className="date">{date}</div>
  )
}

class MainTasks extends React.Component{
  constructor(props){
    super(props);
    this.handleAdd = this.handleAdd.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleRemove = this.handleRemove.bind(this);
    this.changeStatus = this.changeStatus.bind(this);
    this.toggleFilter = this.toggleFilter.bind(this);
    this.state={
      tasks: [
        {id: 1, status:0 , content: 'Client meeting @ 2:30pm '},
      ],
      text: '',
      filterStatus:0
  }
}
  toggleFilter(status){
    this.setState({filterStatus:status*1});
  }

  handleChange(e){
    this.setState({
      text: e.target.value
    })
  }

  handleAdd(e){
    e.preventDefault();
    if(this.state.text.trim() !== '' ){
      const task = {
        id: uuidv4(),
        content: this.state.text,
        status:0
      }
      let tasks = [...this.state.tasks];
      tasks.push(task);

      this.setState({tasks: tasks});

      this.setState({text:''});
    } else {alert('Please fill in the input')}
  }
  handleRemove(uid){
    let tasks =[...this.state.tasks];
    for (const task of tasks){
      if(task.id === uid){
        let removeitem = tasks.map((item) => item.id).indexOf(task.id);
        tasks.splice(removeitem,1);
        this.setState({tasks: tasks});
      }
    }
  }
  changeStatus(id){
    let tasks =[...this.state.tasks];
    for (const task of tasks){
      if(task.id === id){
        task.status = 1;
      }
    }
    this.setState({tasks:tasks})
  }

  render(){
    return (
      <div className='row'>
        <div className='col-lg-10 offset-lg-1 bg-black'>
          <div className="row">
            <div className="col-lg-7">
              <div className="heading-left">
                <DateTime/>
                <div className="count-active-tasks">
                  {this.state.tasks.length} Active Tasks
                </div>
              </div>
            </div>
            <div className="col-lg-5">
              <div className="row">
                  <div className={classnames('col-lg-6 text-center task-filter', this.state.filterStatus === 0 && "active")} onClick={() => this.toggleFilter(0)}>Incomplete Tasks</div>
                  <div className={classnames('col-lg-6 text-center task-filter', this.state.filterStatus === 1 && "active")} onClick={() => this.toggleFilter(1)}>Completed Tasks </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-lg-7">
              <form className="form-custom" onSubmit={this.handleAdd}>
                <input className="input-task" placeholder="Enter a task..." type="text" 
                  onChange={this.handleChange}
                  value={this.state.text}
                />
                <button type="submit" className="btn-add" >Add Tasks</button>
              </form>
            </div>
            <div className="col-lg-5"></div>
            <div className="col-lg-12 mt-lg-4" >
              {
                this.state.tasks.map(value => {
                  if(value.status === this.state.filterStatus){
                    return(
                    <div className= "row taskk" key={value.id}>
                      <div className="col-lg-1">
                        <a href="#" className="i-check" onClick={this.changeStatus.bind(this,value.id)} ><i className="fa fa-check" aria-hidden="true"></i></a>
                      </div>
                      <div className=" col-lg-10" > {value.content } 
                      </div>
                      <div className="col-lg-1 text-right">
                        <a href="#" className={classnames('i-trash',value.status === 1 ? 'd-lg-none':'d-lg-inline')} onClick={this.handleRemove.bind(this,value.id)}><i className="fa fa-trash-o " aria-hidden="true"></i></a>
                      </div>
                    </div>
                  )
                  }
                  return null
                }
                )
              }
            </div>
        </div>
      </div>
    </div>
    )
  }
}

class App extends Component {
  render() {
    return (
      <div className='row'>
        <div className='col-lg-12'>
          <div className='d-md-flex flex-column justify-content-center custom-bg'>
            <MainTasks/>
          </div>
        </div>
      </div>
    )
  }
}

export default App
