import React, { Component } from 'react'

function DateTime(){
  const options = { weekday: 'long', month: 'long', day: 'numeric' };
  const date = new Date().toLocaleDateString(undefined,options);
  return(
    <div className="date">{date}</div>
  )
}

class addTask extends React.Component{
  constructor(props){
    super(props);
    this.handleAdd = this.handleAdd.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.state={task:['a','b']}
  }
  handleChange(e){
    this.setState({task:[e.target.value]})
  }
  handleAdd(e){
    e.preventDefault();
    this.state.task.push('c');
    console.log(this.state.task)
  }
  render(){
    return (
      <button value="submit" type="submit" onClick={this.handleAdd}>Add</button>
    )
  }
}

class App extends Component {
  render() {
    return (
      <div className='row'>
        <div className='col-lg-12'>
          <div className='d-md-flex flex-column justify-content-center custom-bg'>
            <div className='row'>
              <div className='col-lg-8 offset-lg-2 bg-black'>
                  <div className="row">
                    <div className="col-lg-7">
                      <div className="heading-left">
                        <DateTime/>
                        <addTask/>
                      </div>
                    </div>
                    <div className="col-lg-5">
                      <div className="row">
                        <div className="col-lg-6 task">Incomplete Tasks</div>
                        <div className="col-lg-6 task">Complete Tasks</div>
                      </div>
                    </div>
                    <div className="col-lg-12">

                    </div>
                  </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default App
