import "antd/dist/antd.css";

const AddTodo = ({dispatch}) => {
  let input
  return (
      <Col lg={{span:14, offset:1}}>
        <form className="form-custom" onSubmit={e => {
            e.preventDefault(); 
            // if(!input.value.trim()){
            //   return
            // }
            // dispatch(addTodo(input.value))
            console.log(1)
          }}>
          <Form.Item rules={[{required:true,message:'Please fll to input'}]}>
          <Input className="input-task" placeholder="Enter a task..." type="text" 
             ref={node => (input = node)}
          />
          <DatePicker className="input-task ml-10px" />
          </Form.Item>
          <button type="submit" className="btn-add" >Add Tasks</button>
        </form>
      </Col>
  )
}

export default connect()(AddTodo)
