import React from 'react'
import { connect } from 'react-redux'
import { addTodo } from '../actions'
import { DatePicker, Form, Input, Button, Col } from 'antd';
import "antd/dist/antd.css";

const AddTodo = () => {
  return (
      <Col lg={{span:14, offset:1}}>
        <form className="form-custom"

        >
          <Input 
            className="input-task" 
            placeholder="Enter a task..." 
            type="text" 

          />
          <DatePicker className="input-task ml-10px" />
          <Button htmlType="submit" className="btn-add" >Add Tasks</Button>
        </form>
      </Col>
  )
}

const mapDispatchToProps = (dispatch) => {
  return{
    addTodo : (id,content) => dispatch(addTodo(id,content))
  }
}

export default connect(null,mapDispatchToProps)(AddTodo)
