import React from 'react'
import { connect } from 'react-redux'
import { addTodo } from '../actions'

const AddTodo = ({dispatch}) => {
  let input
  let deadline
  return (
      <div className="col-lg-7">
        <form className="form-custom" onSubmit={e => {
            e.preventDefault(); 
            if(!input.value.trim() && isNaN(Date.parse(deadline.value)) == false){
              return
            }
            dispatch(addTodo(input.value,deadline.value))
            input.value =''
            deadline.value=''
          }}>
          <input className="input-task" placeholder="Enter a task..." type="text" 
             ref={node => (input = node)}
          />
          <input className="input-task ml-10px" placeholder="Enter a deadline..." type="text" 
            ref={dead => (deadline = dead)}
          />
          <button type="submit" className="btn-add" >Add Tasks</button>
        </form>
      </div>
  )
}

export default connect()(AddTodo)
