//     </div>
//   )
// }

function UserGreeting(props){
  return(
    <h1>Welcome!</h1>
  )
}

function GuestGreeting(props){
  return(
    <h1>Please sign up</h1>
  )
}

function Greeting(props){
  const isLongedIn = props.isLongedIn;
  if(isLongedIn){
    return <UserGreeting/>
  }
  return <GuestGreeting/>
}

// function App(){
//   return(
//     <div>
//       <Greeting isLongedIn={false}/>
//     </div>
//   )
// }

function LoginButton(props){
  return(
    <button onClick={props.onClick}>Login</button>
  )
}

function LogoutButton(props){
  return(
    <button onClick={props.onClick}>Logout</button>
  )
}

class LoginControll extends React.Component{
  constructor(props){
    super(props);
    this.handleLogoutButton = this.handleLogoutButton.bind(this);
    this.handleLoginButton = this.handleLoginButton.bind(this);
    this.state = {isLongedIn:false}
  }
  handleLoginButton(){
    this.setState({isLongedIn:true})
  }
  handleLogoutButton(){
    this.setState({isLongedIn:false})
  }
  render(){
    const isLongedIn = this.state.isLongedIn;
    let button;
    if(isLongedIn){
      button = <LogoutButton onClick={this.handleLogoutButton}/>
    } else {
      button = <LoginButton onClick={this.handleLoginButton}/>
    }
    return(
      <div>
        <Greeting isLongedIn={isLongedIn}/>
        {button}
      </div>
    )
  }
}

