import React, { Component } from 'react'

function DateTime(){
  const options = { weekday: 'long', month: 'long', day: 'numeric' };
  const date = new Date().toLocaleDateString(undefined,options);
  return(
    <div className="date">{date}</div>
  )
}
class AddTask extends React.Component{
  constructor(props){
    super(props);
    this.handleAdd = this.handleAdd.bind(this);
    this.state={task:[]}
  }
  handleAdd(e){
    const valueinput = document.getElementById("task").value;
    e.preventDefault();
    if(valueinput != ''){
      this.state.task.push(valueinput);
      console.log(this.state.task)
    } else {

    }
  }
  render(){
    return (
      <form>
        <input type="text"  id="task" onChange={this.handleChange}/>
        <button href="#" onClick={this.handleAdd}>Add</button>
      </form>
    )
  }
}

class App extends Component {
  render() {
    return (
      <div className='row'>
        <div className='col-lg-12'>
          <div className='d-md-flex flex-column justify-content-center custom-bg'>
            <div className='row'>
              <div className='col-lg-8 offset-lg-2 bg-black'>
                  <div className="row">
                    <div className="col-lg-7">
                      <div className="heading-left">
                        <DateTime/>
                      </div>
                    </div>
                    <div className="col-lg-5">
                      <div className="row">
                        <div className="col-lg-6 task">Incomplete Tasks</div>
                        <div className="col-lg-6 task">Complete Tasks</div>
                      </div>
                    </div>
                    <div className="col-lg-12">
                      <AddTask/>
                    </div>
                  </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default App
