import React from 'react'
import classnames from 'classnames'
import { Row, Col } from 'antd';

const ToggleFilter = () =>{
    let filter = 1;
    return(
        <Row>
        <Col span={6} className={classnames(' text-center task-filter', filter === 1 ? 'active' : '')} onClick={() => filter = 1 }>Incomplete Tasks</Col>
        <Col span={6} className={classnames(' text-center task-filter', filter === 2 ? 'active' : '')} onClick={() => filter = 2}>Completed Tasks </Col>
      </Row>
    )
}

export default ToggleFilter
