import React, { Component } from 'react'

function DateTime(){
  const options = { weekday: 'long', month: 'long', day: 'numeric' };
  const date = new Date().toLocaleDateString(undefined,options);
  return(
    <div className="date">{date}</div>
  )
}

class MainTasks extends React.Component{
  constructor(props){
    super(props);
    this.handleAdd = this.handleAdd.bind(this);
    this.state={tasks:{task:{id:1,content:'abcd'}}}
  }
  handleAdd(e){
    const valueinput = document.getElementById("task").value;
    e.preventDefault();
    if(valueinput !== ''){

      console.log(this.state.tasks)
    } else {
      alert('Just text or number')
    }
  }
  render(){
    return (
      <div className="row">
        <div className="col-lg-7">
          <form className="form-custom">
            <input className="input-task" placeholder="Enter a task..." type="text"  id="task" onChange={this.handleChange}/>
            <button className="btn-add"  onClick={this.handleAdd}>Add Tasks</button>
          </form>
        </div>
        <div className="col-lg-5"></div>
        <div className="col-lg-12">

        </div>
      </div>
    )
  }
}

class App extends Component {
  render() {
    return (
      <div className='row'>
        <div className='col-lg-12'>
          <div className='d-md-flex flex-column justify-content-center custom-bg'>
            <div className='row'>
              <div className='col-lg-10 offset-lg-1 bg-black'>
                  <div className="row">
                    <div className="col-lg-7">
                      <div className="heading-left">
                        <DateTime/>
                      </div>
                    </div>
                    <div className="col-lg-5">
                      <div className="row">
                        <div className="col-lg-6 task">Incomplete Tasks</div>
                        <div className="col-lg-6 task">Complete Tasks</div>
                      </div>
                    </div>
                  </div>

                        <MainTasks/>

              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default App
