import React from 'react'
import { connect } from 'react-redux'
import { toggleFilter } from '../actions'
import classnames from 'classnames'

const ToggleFilter = ({dispatch}) =>{

    return(
        <div className="row">
        <div className={classnames('col-md-6 text-center task-filter')} onClick={() => toggleFilter(true)}>Incomplete Tasks</div>
        <div className={classnames('col-md-6 text-center task-filter')} onClick={() => toggleFilter(false)}>Completed Tasks </div>
      </div>
    )
}

export default connect()(ToggleFilter)
